       function isAuth(req) {
    
    const data = {
        token : '',
        userID: '',
        message : ''
    };
    const authorization = req.headers['authorization'];
    if(authorization === undefined) {
        data.message = 'You need to login';
    } else  {
        data.token = authorization.split(' ')[1];
        data.userID = JWT.verify(data.token, process.env.ACCESS_TOKEN_SECRET);
    }
    

    return data;
}
